definition.script:
import groovy.json.JsonSlurperClassic
node {
    environment {
        String input_or_not = "[0]";
        String compile_result = "";
        String test_result="";
    }
    stage('Clone the Git') {
        String url = "http://120.108.204.152:10085/root/"+ env.JOB_BASE_NAME +".git"
    git branch: 'main', credentialsId: '2559ce4a-61fa-4a9b-af93-23a90be8dd9b', url: url
  }
  stage('Prepare testfile'){
    env.WORKSPACE = pwd()
    String NAME = env.JOB_BASE_NAME.substring(0, env.JOB_BASE_NAME.indexOf('_'))
    echo "Project name is ${NAME}"

    String response = sh(script: "curl -s 'http://120.108.204.152:3000/api/question1/${NAME}'", returnStdout: true).trim()
    echo "${response}"
    def json = new groovy.json.JsonSlurperClassic().parseText(response)
    env.input_or_not = json.input_or_not;
    if(env.input_or_not.equals("[0]")){
        org.jenkinsci.plugins.pipeline.modeldefinition.Utils.markStageSkippedForConditional('Prepare testfile')
    }else{

    if(fileExists("${env.WORKSPACE}/../testfile")){
        echo "Directory ${env.WORKSPACE}/../testfile exists."
    }else{
        echo " Directory ${env.WORKSPACE}/../testfile doesn't exists. "
        echo " Create testfile directory. "
        def file = new File("${env.WORKSPACE}/../testfile")
        file.mkdir()
    }

    if(fileExists("${env.WORKSPACE}/../testfile/${NAME}")){
        echo "Directory ${env.WORKSPACE}/../testfile/${NAME} exists."
    }else{
        echo " Directory ${env.WORKSPACE}/../testfile/${NAME} doesn't exists. "
        def testfile = new File("${env.WORKSPACE}/../testfile/${NAME}")
        testfile.mkdir()
    }

    def AppTest = new File("${env.WORKSPACE}/../testfile/${NAME}/AppTest.java")
    AppTest.createNewFile()


    dir('../'){ 
        sh "javac -cp json-20220320.jar testfile_generator.java"
        sh "java -cp '.:json-20220320.jar' testfile_generator ${NAME}"
    }

    String testfile = readFile "${env.WORKSPACE}/../testfile/${NAME}/AppTest.java"

    echo testfile

    writeFile(file: "${env.WORKSPACE}/src/test/java/edu/selab/AppTest.java" , text: testfile)
    sh "cat ${env.WORKSPACE}/src/test/java/edu/selab/AppTest.java"
    }
  }

      stage('Compile'){
      def mvnHome = tool name: 'maven',type : 'maven'
      try {
        sh "${mvnHome}/bin/mvn clean compile"
        env.compile_result="1";
      } catch (err) {
        echo "compile fail"
        env.compile_result="0";
      }

    }
    stage('Test'){
        if(env.input_or_not.equals("[0]")){
        org.jenkinsci.plugins.pipeline.modeldefinition.Utils.markStageSkippedForConditional('Test')
    }else{
        try {
        def mvnHome = tool name: 'maven',type : 'maven'
        sh "${mvnHome}/bin/mvn clean test"
        env.test_result="1"
        } catch (err) {
        echo "test fail"
        env.test_result="0";
      }
    }
    }
  stage('SonarQube analysis') {
    def scannerHome = tool 'sonarqube';
    def mvnHome = tool name: 'maven',type : 'maven'
    withSonarQubeEnv('sonarqube') {
         sh "${mvnHome}/bin/mvn sonar:sonar"
    }
  }
  stage('Get Sonar report'){
      //echo '${env.WORKSPACE}/target/surefire-reports/TEST-*.xml'
    def summary = junit testResults: '**/target/surefire-reports/TEST-*.xml'
     echo "${summary.totalCount}"
     echo "${summary.failCount}"
     echo "${summary.passCount}"
    env.test_result="Total:"+summary.totalCount+",Failures:"+summary.failCount+",Passed:"+summary.passCount
    echo test_result
    String NAME =  env.JOB_BASE_NAME
    String submit_times = env.BUILD_NUMBER    
    dir('../'){ 
        sh "javac -cp json-20220320.jar sonarqube_report_analyzer.java"
        sh "java -cp '.:json-20220320.jar' sonarqube_report_analyzer ${NAME} ${submit_times} ${env.test_result} ${env.compile_result}"
    }
  }
  stage('Post Process'){
    String NAME =  env.JOB_BASE_NAME
    dir('../'){ 
        sh "rmdir ${NAME}@tmp"
    }
  }

}
